import React from 'react';
import {
  Button,
  FormControl,
  Grid,
  InputLabel,
  MenuItem,
  Paper,
  Select,
  TextField,
  Typography,
} from '@material-ui/core';

import * as yup from 'yup';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import SaveIcon from '@material-ui/icons/Save';

import { useStyles } from './css/main';

export default function Register() {
  const classes = useStyles();

  /* doSubmit = async () => {
    const { data } = this.state;
  }; */

  const [firstName, setFirstName] = React.useState('');
  const [lastName, setLastName] = React.useState('');
  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');
  const [restOwner, setRestOwner] = React.useState(false);
  const [profilePicture, setProfilePicture] = React.useState('');

  const validateUserOnRegister = async (user) => {
    let userSchema = yup.object().shape({
      firstName: yup
        .string()
        .required('יש לציין שם פרטי')
        .trim()
        .min(2, 'שם פרטי קצר מדי')
        .max(15, 'שם פרטי ארוך מדי'),

      lastName: yup
        .string()
        .required('יש לציין שם משפחה')
        .trim()
        .min(2, 'שם המשפחה קצר מדי')
        .max(15, 'שם המשפחה ארוך מדי'),

      email: yup
        .string()
        .required('יש לציין אימייל')
        .trim()
        .min(6, 'האימייל קצר מדי')
        .max(20, 'האימייל ארוך מדי')
        .email()
        .lowercase(),

      password: yup
        .string()
        .required('יש לציין סיסמה')
        .min(8, 'הסיסמה קצרה מדי')
        .max(20, 'הסיסמה ארוכה מדי'),

      restOwner: yup.boolean().required('יש לציין האם את/ה בעל/ת מסעדה'),
    });

    // check validity
    // abortEarly make validation for all fields and send all errors instead of one at a time
    return userSchema.validate(user, { abortEarly: false });
  };

  return (
    <Grid
      container
      direction="row"
      justify="center"
      alignItems="center"
      className={classes.registerContainer}
    >
      <Grid item>
        <Paper elevation={24} className={classes.registerPaperClass}>
          <pre>
            {firstName} <br />
            {lastName} <br />
            {email} <br />
            {password} <br />
            {restOwner} <br />
            {profilePicture} <br />
          </pre>
          <form
            // onSubmit={this.handleSubmit}
            method="POST"
            autoComplete="off"
            className={classes.form}
          >
            <Typography variant="h6" className={classes.root}>
              נא הירשם כדי להיות חלק ממסעדה!
            </Typography>

            <TextField
              className={classes.registerInput}
              id="outlined-basic"
              label="שם פרטי"
              variant="outlined"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
            />

            <TextField
              id="outlined-basic"
              label="שם משפחה"
              variant="outlined"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
            />

            <TextField
              id="outlined-basic"
              label="אימייל"
              variant="outlined"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <br />

            <TextField
              id="outlined-basic"
              label="סיסמה"
              variant="outlined"
              value={password}
              onChange={(e) => setEmail(e.target.value)}
            />

            <br />
            {/* DROP DOWN SELECT/OPTION */}

            <FormControl
              style={{ minWidth: '222px' }}
              variant="outlined"
              className={classes.formControl}
            >
              <InputLabel id="demo-simple-select-outlined-label">
                בעלים של מסעדה?
              </InputLabel>
              <Select
                labelId="demo-simple-select-outlined-label"
                id="demo-simple-select-outlined"
                value={restOwner}
                onChange={setRestOwner}
                label="Age"
              >
                <MenuItem value={true}>כן</MenuItem>
                <MenuItem value={false}>לא</MenuItem>
              </Select>
            </FormControl>
            <br />

            <input
              accept="image/*"
              className={classes.input}
              id="contained-button-file"
              multiple
              type="file"
              style={{ display: 'none' }}
              value={profilePicture}
              onChange={setProfilePicture}
            />
            <label htmlFor="contained-button-file">
              <Button
                variant="contained"
                color="primary"
                component="span"
                startIcon={<CloudUploadIcon style={{ minWidth: '40px' }} />}
                // innerDivStyle={{ paddingRight: 60 }}
                className={classes.register_button}
              >
                העלה תמונת פרופיל
              </Button>
            </label>

            <br />
            {/* Send */}
            <Button
              variant="contained"
              color="secondary"
              size="large"
              className={classes.button}
              startIcon={<SaveIcon />}
            >
              שלח
            </Button>
          </form>
        </Paper>
      </Grid>
    </Grid>
  );
}
