import { makeStyles, fade } from '@material-ui/core/styles';
import { green } from '@material-ui/core/colors';
import homeSlider from '../images/homeSliderOpacity50p.png';

export const useStyles = makeStyles((theme) => ({
  minHeight: {
    minHeight: '700px',
  },
  homeSlider: {
    // position: 'absolute',
    // backgroundAttachment: '../images/homeSliderOpacity50p.png',
    backgroundImage: `url(${homeSlider})`,
    height: '54.5rem',
    objectFit: 'cover',
    zIndex: -500,
  },
  sliderText: {
    textAlign: 'left',
  },
  sliderTitleText: {
    fontFamily: 'Suez One',
    marginLeft: theme.spacing(8),
    fontSize: '3rem',
    color: '#145B78',
    textShadow: '2px 2px 5px #ffffff',
  },
  sliderSmallTitleText: {
    fontFamily: 'Suez One',
    marginLeft: theme.spacing(8),
    //opacity: 0.7,
    color: '#5E0F1E',
    fontSize: '2rem',
    textShadow: '2px 2px 5px #ffffff',
  },

  rankTitleText: {
    fontFamily: 'Suez One',
    fontSize: '2.2rem',
    //opacity: 0.99,
    color: '#969633',
    textShadow: '1px 1px 2.5px #ffffff',
  },

  /* Search Input */
  searchContainer: {
    position: 'absolute',
    marginRight: theme.spacing(1),
  },
  search: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    marginRight: theme.spacing(0),
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(3),
      width: 'auto',
    },
  },
  searchIcon: {
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputRoot: {
    color: 'inherit',
  },
  inputInput: {
    padding: theme.spacing(1, 6, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('md')]: {
      width: '20ch',
    },
  },
  /* / Search Input */

  /* Ranked Card */

  card: {
    //maxWidth: "260px",
    minWidth: '263px',
    //opacity: 0.9,
  },

  media: {
    margin: '0 auto',
    /* Desired image size: width: 208, height: 210 */
    width: '208px',
    height: '210px',
    overflow: 'hidden',
  },

  image: {
    minHeight: '100%',
    minWidth: '100%',
  },

  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: green[400],
  },
  cardContent: {
    textAlign: 'left',
    fontSize: '1rem',
  },
  cardContentSpan: {
    fontWeight: 'bold',
  },
  description: {
    maxWidth: 228,
    overflowWrap: 'anywhere',
  },

  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },

  shareButtonMenu: {
    position: 'absolute',
    bottom: '55em',
  },

  /* Register */
  registerContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },

  registerPaperClass: {
    minHeight: '60ch',
    maxHeight: '90ch',
    maxWidth: '40ch',
    margin: '0 auto',
  },
  form: {},
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  register_button: {
    margin: theme.spacing(1),
  },

  /* Links */

  active: {
    color: 'magenta',
    fontWeight: 'bold',
  },
}));
