import { makeStyles } from '@material-ui/core/styles';
import { green } from '@material-ui/core/colors';
/* import homeSlider from '../images/homeSlider.jpg'; */

export const useStyles = makeStyles((theme) => ({
  body: {
    minHeight: '700px',
  },
  homeSlider: {
    position: 'absolute',
    height: '40rem',
    objectFit: 'cover',
    opacity: 0.5,
  },
  sliderText: {
    textAlign: 'left',
  },
  sliderTitleText: {
    fontFamily: 'Suez One',
    marginLeft: theme.spacing(8),
    fontSize: '3rem',
    color: '#145B78',
    opacity: 0.8,
    textShadow: '2px 2px 5px #ffffff',
  },
  sliderSmallTitleText: {
    fontFamily: 'Suez One',
    marginLeft: theme.spacing(8),
    opacity: 0.7,
    color: '#5E0F1E',
    fontSize: '2rem',
    textShadow: '2px 2px 5px #ffffff',
  },

  rankTitleText: {
    fontFamily: 'Suez One',
    fontSize: '2.2rem',
    opacity: 0.99,
    color: '#969633',
    textShadow: '1px 1px 2.5px #ffffff',
  },

  /* Ranked Card */

  card: {
    //maxWidth: "260px",
    minWidth: '263px',
    opacity: 0.9,
  },

  media: {
    margin: '0 auto',
    /* Desired image size: width: 208, height: 210 */
    width: '208px',
    height: '210px',
    overflow: 'hidden',
  },

  image: {
    minHeight: '100%',
    minWidth: '100%',
  },

  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: green[400],
  },
  cardContent: {
    textAlign: 'left',
    fontSize: '1rem',
  },
  cardContentSpan: {
    fontWeight: 'bold',
  },
  description: {
    maxWidth: 228,
    overflowWrap: 'anywhere',
  },

  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },

  shareButtonMenu: {
    position: 'absolute',
    bottom: '55em',
  },
}));
